public class EvenNumbersUsingForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i;
		
		System.out.println("The first 10 even numbers are : ");
		
		for(i=1;i<=10;i++)
		{
			int EvenNumbers = i*2;
			System.out.println(EvenNumbers);
		}
	}

}
